package net.jeeshop.services.manage.secureProduct.dao.impl;import java.util.List;import net.jeeshop.core.dao.BaseDao;import net.jeeshop.core.dao.page.PagerModel;import net.jeeshop.services.manage.orderdetail.bean.Orderdetail;import net.jeeshop.services.manage.secureProduct.bean.SecureProduct;import net.jeeshop.services.manage.secureProduct.dao.SecureProductDao;import org.springframework.stereotype.Repository;import javax.annotation.Resource;@Repository("secureProductDaoManage")public class SecureProductDaoImpl implements SecureProductDao {    @Resource	private BaseDao dao;	public void setDao(BaseDao dao) {		this.dao = dao;	}	public PagerModel selectPageList(SecureProduct e) {		return dao.selectPageList("manage.product.selectPageList",				"manage.product.selectPageCount", e);	}	public List selectList(SecureProduct e) {		return dao.selectList("manage.product.selectList", e);	}	public SecureProduct selectOne(SecureProduct e) {		return (SecureProduct) dao.selectOne("manage.product.selectOne", e);	}	public int delete(SecureProduct e) {		return dao.delete("manage.product.delete", e);	}	public int update(SecureProduct e) {		return dao.update("manage.product.update", e);	}	public int deletes(String[] ids) {		SecureProduct e = new SecureProduct();		for (int i = 0; i < ids.length; i++) {			e.setId(ids[i]);			delete(e);		}		return 0;	}	public int insert(SecureProduct e) {		return dao.insert("manage.product.insert", e);	}	public int deleteById(int id) {		return dao.delete("manage.product.deleteById", id);	}	public SecureProduct selectById(String id) {		return (SecureProduct) dao.selectOne("manage.product.selectById", id);	}	@Override	public void deleteAttributeLinkByProductID(int parseInt) {		dao.delete("manage.product.deleteAttributeLinkByProductID", parseInt);			}	@Override	public List<SecureProduct> selectStockByIDs(List<String> productIDs) {		return dao.selectList("manage.product.selectStockByIDs",productIDs);	}	@Override	public int selectOutOfStockProductCount() {		return (Integer) dao.selectOne("manage.product.selectOutOfStockProductCount");	}	@Override	public void updateImg(SecureProduct p) {		dao.update("manage.product.updateImg",p);	}	@Override	public void updateProductStatus(SecureProduct p) {		dao.update("manage.product.updateProductStatus",p);	}	@Override	public void updateProductBindActivityId(SecureProduct pro) {		dao.update("manage.product.updateProductBindActivityId",pro);	}	@Override	public void updateResetThisProductActivityID(String activityID) {		dao.update("manage.product.updateResetThisProductActivityID",activityID);	}}