package net.jeeshop.web.action.manage.SecureOrder;import java.util.List;import javax.servlet.http.HttpSession;import org.slf4j.LoggerFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import net.jeeshop.services.manage.keyvalue.KeyvalueService;import net.jeeshop.services.manage.keyvalue.bean.Keyvalue;import net.jeeshop.services.manage.keyvalue.dao.impl.KeyvalueDaoImpl;import net.jeeshop.services.manage.secureOrder.SecureOrderService;import net.jeeshop.services.manage.secureOrder.bean.SecureOrder;import net.jeeshop.web.action.BaseController;import net.jeeshop.web.util.RequestHolder;/** * @author lin 订单管理 */@Controller@RequestMapping("/manage/SecureOrder/")public class SecureOrderAction extends BaseController<SecureOrder> {	private static final org.slf4j.Logger logger = LoggerFactory.getLogger(SecureOrderAction.class);	private static final long serialVersionUID = 1L;	private static final String page_toList = "/manage/SecureOrder/SecureOrderList";		private SecureOrderAction() {		super.page_toList = page_toList;	}		@Autowired	private SecureOrderService secureOrderService;		@Autowired	private KeyvalueService keyValueService;	@Override	public SecureOrderService getService() {		return secureOrderService;	}	public void setOrderService(SecureOrderService secureOrderService) {		this.secureOrderService = secureOrderService;	}		/**	 * selectList 注入事件  初始化select	 */	public void beforeSelectList(){		Keyvalue kv = new Keyvalue();		kv.setP_code("Currency");		List list = keyValueService.selectList(kv);		HttpSession sess = RequestHolder.getSession();		if(sess.getAttribute("dic_map")!=null){			sess.removeAttribute("dic_map");		}		sess.setAttribute("dic_map", list);	}}