package net.jeeshop.web.action.manage.SecureOrder;import org.slf4j.LoggerFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import net.jeeshop.services.manage.order.OrderService;import net.jeeshop.services.manage.secureOrder.SecureOrderService;import net.jeeshop.services.manage.secureOrder.bean.SecureOrder;import net.jeeshop.web.action.BaseController;/** * @author lin 订单管理 */@Controller@RequestMapping("/manage/SecureOrder/")public class SecureOrderAction extends BaseController<SecureOrder> {	private static final org.slf4j.Logger logger = LoggerFactory.getLogger(SecureOrderAction.class);	private static final long serialVersionUID = 1L;	private static final String page_toList = "/manage/SecureOrder/SecureOrderList";		private SecureOrderAction() {		super.page_toList = page_toList;	}		@Autowired	private SecureOrderService secureOrderService;	@Override	public SecureOrderService getService() {		return secureOrderService;	}	public void setOrderService(SecureOrderService secureOrderService) {		this.secureOrderService = secureOrderService;	}}