mapper.select:
select n.id,n.SerialNumber,IFNULL(DATEDIFF(NOW(),n.TInsrncEndTm),'') expire_days, 
n.CProdName,n.CAppNo,n.status,n.CProdNo,n.appCde appCdePlay,n.NAmt,n.NPrm,n.TAppTm,n.TInsrncBgnTm,n.TInsrncEndTm,n.createtime,
(select a.AppNme from  nvhlapplicantvo a where A.AppCde=n.AppCde  and a.createAccount = n.createAccount )AppNme ,
(select i.InsuredNme from  nvhlinsuredvo i where i.InsuredCde=n.InsuredCde and i.createAccount = n.createAccount )InsuredNme ,u.nickname username 
from nvhlbasevo n ,nvhlapplicantvo a,nvhlinsuredvo i,t_user u   where 1=1 and deleteFlag='0'
and n.AppCde = a.AppCde and n.InsuredCde=i.InsuredCde and n.createAccount = u.id

	and n.createAccount=#{createAccount}


	and n.AppCde=#{AppCde}


	and TInsrncEndTm >= #{TInsrncEndTmStartDate}


	and TInsrncEndTm <= #{TInsrncEndTmEndDate}
		

	and n.CProdName like CONCAT('%','${CProdName}','%' )


	and n.payNo = #{payNo}

group by n.SerialNumber
order by createtime desc,TAppTm,TInsrncEndTm 	
	limit #{offset},#{pageSize}

select * from nvhlbasevo p where 1=1 

	and payNo=#{payNo}
	

	and serialNumber=#{serialNumber}


	and id=#{id}

select count(*) from nvhlbasevo  d where 1=1

	and createAccount=#{createAccount}


	and TInsrncEndTm >= #{TInsrncEndTmStartDate}


	and TInsrncEndTm <= #{TInsrncEndTmEndDate}


	and CProdName like CONCAT('%','${CProdName}','%' )

mapper.update:
update nvhlbasevo
set
updatetime=now()

	,updateAccount=#{updateAccount}
		

	,status=#{status}


	,payNo=#{payNo}

where 1=1

	and serialNumber=#{serialNumber}

		update nvhlbasevo
		set
		updatetime=now(),updateAccount=#{updateAccount}
		
			,status=#{status}
		
		
			,cashNo=#{cashNo}
		
		
			,payType=#{payType}
		
		
			,bankAcctDate=#{bankAcctDate}
		
		
		
			,bankCode=#{bankCode}
		
		
			,tidNo=#{tidNo}
		

		
			,bkSerial=#{bkSerial}
		
		
			,cardNo=#{cardNo}
		
		
			,remark=#{remark}
		
		where 1=1
		
			and payNo=#{payNo}
